CREATE TABLE group_product (id BIGINT AUTO_INCREMENT, group_id SMALLINT DEFAULT 0 NOT NULL, product_id SMALLINT DEFAULT 0 NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE player (id SMALLINT AUTO_INCREMENT, name VARCHAR(32), profit FLOAT(18, 2) DEFAULT 0, token VARCHAR(255) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE product (id SMALLINT AUTO_INCREMENT, producer SMALLINT DEFAULT 0 NOT NULL, consumer SMALLINT DEFAULT 0 NOT NULL, holder SMALLINT DEFAULT 0 NOT NULL, cost FLOAT(18, 2) DEFAULT 0 NOT NULL, utility FLOAT(18, 2) DEFAULT 0 NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE transaction (id BIGINT AUTO_INCREMENT, type TINYINT, from_id SMALLINT DEFAULT 0 NOT NULL, to_id SMALLINT DEFAULT 0 NOT NULL, refer_id BIGINT DEFAULT 0, product SMALLINT DEFAULT 0 NOT NULL, price FLOAT(18, 2) DEFAULT 0 NOT NULL, first_ref_fee FLOAT(18, 2) DEFAULT 0 NOT NULL, second_ref_fee FLOAT(18, 2) DEFAULT 0 NOT NULL, ref_degree TINYINT DEFAULT 0, rnd SMALLINT DEFAULT 0, expire SMALLINT DEFAULT 0, status TINYINT DEFAULT 0 NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
