CREATE TABLE group_product (id BIGINT AUTO_INCREMENT, group_id BIGINT DEFAULT 0 NOT NULL, product_id BIGINT DEFAULT 0 NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE player (id SMALLINT AUTO_INCREMENT, name VARCHAR(32), token VARCHAR(255) NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE product (id SMALLINT AUTO_INCREMENT, producer SMALLINT DEFAULT 0 NOT NULL, consumer SMALLINT DEFAULT 0 NOT NULL, cost FLOAT(18, 2) DEFAULT 0 NOT NULL, utility FLOAT(18, 2) DEFAULT 0 NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE transaction (id BIGINT AUTO_INCREMENT, from_id BIGINT DEFAULT 0 NOT NULL, to_id BIGINT DEFAULT 0 NOT NULL, round SMALLINT DEFAULT 0 NOT NULL, status TINYINT(1) DEFAULT '0' NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
